import streamlit as st

# Create two columns: one for the image and one for the text
col1, col2 = st.columns([1, 2])

# Inserting an image with custom size and alignment
st.markdown(
    """
    <style>
    .small-image {
        width: 100px;
        height: auto;
        object-fit: contain;
        float: left;
        margin-right: 10px;
        margin-bottom: 10px;
    }
    </style>
    """,
    unsafe_allow_html=True
)

# Insert the image in the first column
with col1:
    st.image("images/nexus_logo.jpeg", width=100)

# Insert the text in the second column
with col2:
    st.title("Historial de Chats")


def main():

    # Define dummy conversation data
    conversation = [
        {'user': 'Mickey', 'message': '¡Hola! ¿Como estas?', 'time': '8:00 AM', 'profile_picture': 'https://www.vhv.rs/dpng/d/124-1249367_mickey-mouse-back-view-mickey-mouse-from-back.png'},
        {'user': 'Minnie', 'message': '¿Cuanto cuesta?', 'time': '10:05 AM', 'profile_picture': 'data:image/png;base64,'},
        {'user': 'Goofy', 'message': '¿Cuentan con garantia?', 'time': '12:10 PM', 'profile_picture': 'https://pngimg.com/uploads/goofy/goofy_PNG2.png'},
        {'user': 'Pluto', 'message': 'Ya me llegó el producto, muchas gracias', 'time': '4:00 PM', 'profile_picture': 'https://w7.pngwing.com/pngs/698/659/png-transparent-pluto-mickey-mouse-goofy-minnie-mouse-dog-pluto-s-pet-flower-cartoon.png'},
    ]

    # Add CSS styling for the chat bubbles and profile pictures
    st.markdown(
        """
        <style>
        .chat-container {
            display: flex;
            align-items: flex-start;
            width: 100vw;
        }

        .chat-bubble {
            display: flex;
            flex-direction: column;
            padding: 10px;
            margin: 10px;
            width: 40%;
            border-radius: 10px;
            position: relative;
        }

        .user-a {
            background-color: #DCF8C6;
            align-self: flex-start;
        }

        .user-b {
            background-color: #E5E5EA;
            align-self: flex-end;
        }

        .profile-picture {
            width: 30px;
            height: 30px;
            border-radius: 50%;
            margin-right: 10px;
        }

        .message-time {
            position: absolute;
            bottom: -20px;
            right: 0;
            font-size: 10px;
            color: gray;
        }
        </style>
        """,
        unsafe_allow_html=True
    )

    # Display the conversation history with chat bubbles, profile pictures, and message times
    for entry in conversation:
        user = entry['user']
        message = entry['message']
        time = entry['time']
        profile_picture = entry['profile_picture']

        # Apply different CSS classes based on the user
        css_class = "user-a" if user == "User A" else "user-b"

        # Render the chat bubble with the appropriate CSS class, profile picture, and message time
        st.markdown(f"""
            <div class="chat-container">
                <img class="profile-picture" src="{profile_picture}">
                <div class="chat-bubble {css_class}">
                    <div>{user}: {message}</div>
                    <div class="message-time">{time}</div>
                </div>
            </div>
        """, unsafe_allow_html=True)

if __name__ == "__main__":
    main()













